// search videos
yt.searchVideos = function (user_search, page, filters, order, duration, cb){
    videos_responses = new Array();
    var params = {};

    if (filters === '') {
        params = {
                    q:''+user_search+'',
                    maxResults: 25,
                    order: ''+order+'',
                }
    } else if (filters === 'hd') {
        params = {
                    q:              ''+user_search+'',
                    maxResults:  25,
                    order:        ''+order+'',
                    videoDefinition : 'high'
                }
    } else if (filters === '3d') {
        params = {
                    q:              ''+user_search+'',
                    maxResults:  25,
                    order:        ''+order+'',
                    videoDimension : '3d'
                }
    }
    if(duration !== '') {
		params.videoDuration = duration;
	}
    params.part = 'snippet';
    if(page !== 1) {
        params.pageToken = page;
    }
    params.type = 'video';
    try{
        ytapi.search.list(params,
            function( err, data ) {
                console.log(err,data)
            if( err ) {
                cb(err);
            } else {
                console.log(data)
                current_start_index = cb.nextPageToken;
                cb(data);
            }
        });
    }catch(err){
        console.log('searchVideos err: '+err);
    }
}

